import React, { Component } from 'react';
import { withAuth } from '@okta/okta-react';
import InNavbar from '../layout/InNavbar';
import OutNavbar from '../layout/OutNavbar';

export default withAuth(
  class NewSnippet extends Component {
    state = { 
      authenticated: null,
      LANGUAGE_CHOICE : [
        {javascript:'JAVASCRIPT'},
        {css: 'CSS'},
        {'red':'HTML'},
        {'c#':'C#'},
        {'c++':'C++'},
        {'bash': 'BASH'},
        {'apache':'APACHE'},
        {'xml':'XML'},
        {'php':'PHP'},
        {'json':'JSON'},
        {'perl': 'PERL'},
        {'sql': 'SQL'},
        {'ruby': 'RUBY'},
        {'python': 'PYTHON'},
        {'objective-c': 'OBJECTIVE-C'},
        {'diff': 'DIFF'},
        {'java': 'JAVA'},
        {'coffeescript': 'COFFEESCRIPT'},
        {'makefile':'MAKEFILE'},
        {'nginx': 'NGINX'}
      ]
    };

    checkAuthentication = async () => {
      const authenticated = await this.props.auth.isAuthenticated();
      if (authenticated !== this.state.authenticated) {
        this.setState({ authenticated });
      }
    };

    async componentDidMount() {
      this.checkAuthentication();
    }

    async componentDidUpdate() {
      this.checkAuthentication();
    }

    login = async () => {
      this.props.auth.login('/');
    };

    logout = async () => {
      this.props.auth.logout('/');
    };
    getOps = () => {
      let op = [];
      this.state.LANGUAGE_CHOICE.forEach(e=>{
        op.push(<option></option>)
      })
      return op;
    }
    render() {
      if (this.state.authenticated === null) return null;

      const mainContent = this.state.authenticated ? (

        <div>
          <div className="container">
            <InNavbar />
            <form>
              <div className="form-group">
                <label htmlFor="email">Snippet Title</label>
                <input type="text" className="form-control" id="title" />
              </div>
                <div className="form-group">
                  <label htmlFor="language">Code Language</label>
                  <select className="form-control" id="language">
                    {this.getOps()}
                  </select>
                </div>
                <div className="form-group">
                  <label htmlFor="desc">Snippet Description</label>
                  <textarea className="form-control" id="desc" rows="3"></textarea>
                </div>
                <div className="form-group">
                  <label htmlFor="code">Snippet Code</label>
                  <textarea className="form-control" id="code" rows="8"></textarea>
                </div>
                <button type="submit" className="btn btn-primary btn-lg">Snipp it</button>
            </form>
          </div>
        </div>
          ) : (
        <div>
            <OutNavbar />
            <p className="lead">
              If you are a staff member, please get your credentials from your
              supervisor
          </p>
            <button className="btn btn-dark btn-lg" onClick={this.login}>
              Login
          </button>
          </div>
          );

          return (
        <div className="jumbotron">
            {mainContent}
          </div>
          );
        }
      }
);
