import React, { Component } from 'react';
import './App.css';
import {BrowserRouter as Router, Route } from 'react-router-dom'
import {Security, SecureRoute, ImplicitCallback } from '@okta/okta-react'
import Home from './components/pages/Home'
import NewSnippet from './components/pages/NewSnippet'
import Profile from './components/pages/Profile'
import Login from './components/auth/Login'

function onAuthRequired({history}){
  history.push('/login')
}
class App extends Component {
  render() {
    return (

      <Router >
          <Security issuer='https://dev-814615.okta.com/oauth2/default'
                  client_id='0oadj7zxn5fTBBwZx356'
                  redirect_uri={window.location.origin + '/implicit/callback'}
                  onAuthRequired={onAuthRequired} >
          <div className="root">
            <Route path="/" exact={true} component={Home}/>
            <SecureRoute path="/new-snippet" exact={true} component={NewSnippet}/>
            <SecureRoute path="/profile" exact={true} component={Profile}/>
            <SecureRoute path="/snippets" exact={true} component={Home}/>
            <Route path='/login' render={() => <Login baseUrl='https://dev-814615.okta.com' />} />
            <Route path='/implicit/callback' component={ImplicitCallback} />
          </div>
        </Security>
      </Router>
    );
  }
}

export default App;
